import * as React from 'react';
import './App.css';

import SectorSelectorForm from './components/SectorSelectorForm';

function App() {

  // get user session 
  // if user session is not set, redirect to login page
  // if user session is set, render the app

  React.useEffect(() => {
    const userSession = localStorage.getItem('userSession');
    if (!userSession) {
      window.location.href = '/login';
    }
  }, []);

  // set user session
  const setUserSession = (userSession) => {
    localStorage.setItem('userSession', userSession);
  }

  // remove user session
  const removeUserSession = () => {
    localStorage.removeItem('userSession');
  }


  return (
    <div className="App">
      <SectorSelectorForm />
    </div>
  );
}

export default App;
